<script type="text/javascript">
!function(window) {
    window.yumi_partner_tabs = function(obj,type){
        document.getElementById("yumi_partner_type").value = type;
        var className = obj.className;
        if(className.indexOf("yumi_partner-tabs--cur") != -1){//有选中
            
        }else{
            var addedClass = className+" yumi_partner-tabs--cur";
            obj.className = addedClass.replace(/(\s+)/gi, ' ');
            var n = obj.parentNode.firstChild;
            for(; n; n = n.nextSibling) {
                if(n.nodeType === 1 && n !== obj) {
                    var removedClass = n.className.replace("yumi_partner-tabs--cur","");
                    n.className = removedClass;
                }
            }
            yumi_partner_ajax();
        }
    }
    window.yumi_partner_ajax = function(args){
        var topage;
        if(args && typeof(args) == "number"){
            topage = args;
        }else{
            topage = 1;
        }
        
        var post_args = [];
        var type = document.getElementById("yumi_partner_type").value;
        post_args.push("type="+type);
        var domain = document.getElementById("yumi_partner_search_domain").value;
        post_args.push("domain="+encodeURIComponent(domain));
        var start = document.getElementById("yumi_partner_search_start").checked?"on":"";
        post_args.push("start="+start);
        var end = document.getElementById("yumi_partner_search_end").checked?"on":"";
        post_args.push("end="+end);
        var length = document.getElementById("yumi_partner_search_length").value;
        post_args.push("length="+length);
        var type = document.getElementById("yumi_partner_search_type").value;
        post_args.push("elements="+type);
        var suffix = document.getElementById("yumi_partner_search_suffix").value;
        post_args.push("suffix="+suffix);
        
        var industry_arr = [];
        var item_industrys = document.getElementById("yumi_partner_search_industry").children;
        for(var i=0; i<item_industrys.length; i++){
            if(item_industrys[i].nodeName == "DD" && item_industrys[i].className == "cur"){
                industry_arr.push(item_industrys[i].firstChild.getAttribute("data-industry"));
            }
        }
        var industry = industry_arr.join("#");
        post_args.push("industry="+industry);
        
        var price_arr = [];
        var item_prices = document.getElementById("yumi_partner_search_price").children;
        for(var i=0; i<item_prices.length; i++){
            if(item_prices[i].nodeName == "DD" && item_prices[i].className == "cur"){
                price_arr.push(item_prices[i].firstChild.getAttribute("data-price"));
            }
        }
        var price_str = price_arr.join("#");
        post_args.push("price_str="+price_str);
        
        post_args.push("page="+topage);
        post_args.push("page_size=200");
        
        var tbody = document.getElementsByClassName("yumi_partner-table")[0].children[1];
        tbody.innerHTML = '<tr><td colspan="4">查询中，请稍后...</td></tr>';
        
        //步骤一:创建异步对象
        var xhr = new XMLHttpRequest();
        //步骤二:设置请求的url参数,参数一是请求的类型,参数二是请求的url,可以带参数,动态的传递参数starName到服务端
        xhr.open("post","https://www.marksmile.com/fx_yumi_api");
        //post请求一定要添加请求头才行不然会报错,必须在open方法之后
        xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
        //步骤三:发送请求
        xhr.send(post_args.join("&"));
        //步骤四:注册事件 onreadystatechange 状态改变就会调用
        xhr.onreadystatechange = function () {
            if (xhr.readyState==4 &&xhr.status==200) {
                //步骤五 如果能够进到这个判断 说明 数据 完美的回来了,并且请求的页面是存在的
                var result = JSON.parse(xhr.responseText);
                var ajax_type = document.getElementById("yumi_partner_ajax_type");
                if(ajax_type.value == 0){
                    var search_type = document.getElementById("yumi_partner_search_type");
                    var type_option = result.all_elements;
                    var type_option_str = '<option value="">域名分类</option>';
                    for(var i=0; i<type_option.length; i++){
                        var opt_key = type_option[i].split("_")[1];
                        var opt_name = type_option[i].split("_")[0];
                      type_option_str += '<option value="'+opt_key+'">'+opt_name+'</option>';
                    }
                    search_type.innerHTML = type_option_str;
                    ajax_type.value = 1;
                }
                
                var tbody = document.getElementsByClassName("yumi_partner-table")[0].children[1];
                var tbody_str = '';
                var result_list = result.list;
                for (var i=0; i<result_list.length; i++) {
                    var domain_lenth = result_list[i].domain.split(".")[0].length;
                    var domain_explain = result_list[i].explain?result_list[i].explain.replace(/\|/g,'、'):'';
                    tbody_str += '<tr><td class="domain"><a href="http://wpa.qq.com/msgrd?v=3&amp;uin=1003049243&amp;site=qq&amp;menu=yes" target="_blank">'+result_list[i].domain+'</a></td><td class="explain" title="'+domain_explain+'" onclick="yumi_partner_toggleExplain(this)">'+domain_explain+'</td><td class="length">'+result_list[i].length+'</td><td>'+(result_list[i].price?result_list[i].price+'元':'暂无')+'</td><td class="operate"><a href="http://wpa.qq.com/msgrd?v=3&amp;uin=1003049243&amp;site=qq&amp;menu=yes" target="_blank">购买</a></td></tr>';
                }
                if(result_list.length==0){
                    tbody_str += '<tr><td colspan="5">未搜索到相关结果</td></tr>';
                }
                tbody.innerHTML = tbody_str;
                
                var pagination = document.getElementsByClassName("yumi_partner-pagination")[0];
                var page_str = '';
                var result_total = result.total;
                var page_total = Math.ceil(result_total/200);
                var curpage = parseInt(result.curpage);
                page_str += '<ul>';
                if(curpage >1){
                    page_str += '<li><a href="javascript:;" onclick="yumi_partner_ajax('+(curpage-1)+')">上一页</a></li>';
                }
                if(page_total <= 5){
                    for (var i=1; i<=page_total; i++) {
                        if(i == curpage){
                            page_str += '<li class="yumi_partner-pagination--cur"><a href="javascript:;">'+i+'</a></li>';
                        }else{
                            page_str += '<li><a href="javascript:;" onclick="yumi_partner_ajax('+i+')">'+i+'</a></li>';
                        }
                    }
                }else{
                    if(curpage <= 3){
                        for (var i=1; i<=5; i++) {
                            if(i == curpage){
                                page_str += '<li class="yumi_partner-pagination--cur"><a href="javascript:;">'+i+'</a></li>';
                            }else{
                                page_str += '<li><a href="javascript:;" onclick="yumi_partner_ajax('+i+')">'+i+'</a></li>';
                            }
                        }
                    }else if(page_total-curpage <= 3){
                        for (var i=page_total-4; i<=page_total; i++) {
                            if(i == curpage){
                                page_str += '<li class="yumi_partner-pagination--cur"><a href="javascript:;">'+i+'</a></li>';
                            }else{
                                page_str += '<li><a href="javascript:;" onclick="yumi_partner_ajax('+i+')">'+i+'</a></li>';
                            }
                        }
                    }else{
                        for (var i=curpage-2; i<=curpage+2; i++) {
                            if(i == curpage){
                                page_str += '<li class="yumi_partner-pagination--cur"><a href="javascript:;">'+i+'</a></li>';
                            }else{
                                page_str += '<li><a href="javascript:;" onclick="yumi_partner_ajax('+i+')">'+i+'</a></li>';
                            }
                        }
                    }
                }
                if(curpage < page_total){
                    page_str += '<li><a href="javascript:;" onclick="yumi_partner_ajax('+(curpage+1)+')">下一页</a></li>';
                }
                page_str += '</ul>';
                page_str += '<font class="yumi_partner-pagination-total">共 '+result_total+' 条，每页显示 200 条</font>';
                pagination.innerHTML = page_str;
            }
        }
        
    }
    window.yumi_partner_reset = function(args){
        document.getElementById("yumi_partner_search_domain").value = "";
        document.getElementById("yumi_partner_search_start").checked = false;
        document.getElementById("yumi_partner_search_end").checked = false;
        document.getElementById("yumi_partner_search_length").value = "";
        document.getElementById("yumi_partner_search_type").value = "";
        document.getElementById("yumi_partner_search_suffix").value = "";
        
        var item_industrys = document.getElementById("yumi_partner_search_industry").children;
        for(var i=0; i<item_industrys.length; i++){
            if(item_industrys[i].nodeName == "DD"){
                if(i == 1){
                    item_industrys[i].className = "cur";
                }else{
                    item_industrys[i].className = "";
                }
            }
        }
        
        var item_prices = document.getElementById("yumi_partner_search_price").children;
        for(var i=0; i<item_prices.length; i++){
            if(item_prices[i].nodeName == "DD"){
                if(i == 1){
                    item_prices[i].className = "cur";
                }else{
                    item_prices[i].className = "";
                }
            }
        }
        
        yumi_partner_ajax();
    }
    window.yumi_partner_toggleExplain = function(obj){
        if(obj.className.indexOf('explain') != -1){
            obj.className = "";
        }else{
            obj.className = "explain";
        }
    }
    window.yumi_partner_ajax_industry = function(obj){
        var all_obj = document.getElementById("yumi_partner_search_industry_all");
        if(obj == all_obj){//全部
            if(obj.parentNode.className.indexOf("cur") != -1){//如果已经选中的
                return;
            }
            obj.parentNode.className = "cur";
            var n = obj.parentNode.parentNode.firstChild;
            for(; n; n = n.nextSibling) {
                if(n.nodeType === 1 && n !== obj.parentNode) {
                    n.className = "";
                }
            }
        }else{
            if(obj.parentNode.className.indexOf("cur") != -1){//如果已经选中的
                obj.parentNode.className = "";//先取消选中,再判断是否一个选中的都没有
                var n = obj.parentNode.parentNode.firstChild;
                var itemSelectedCount = 0;
                for(; n; n = n.nextSibling) {
                    if(n.nodeType === 1 && n !== all_obj.parentNode) {
                        if(n.className.indexOf("cur") != -1){
                            itemSelectedCount++;
                        }
                    }
                }
                if(itemSelectedCount == 0){//如果一个选中的子项都没有
                    all_obj.parentNode.className = "cur";
                }else{
                    all_obj.parentNode.className = "";
                }
                
            }else{//如果未选中,则选中,并将去掉“全部”的选中状态
                obj.parentNode.className = "cur";
                all_obj.parentNode.className = "";
            }
        }
        yumi_partner_ajax();
    }
    window.yumi_partner_ajax_price = function(obj){
        var all_obj = document.getElementById("yumi_partner_search_price_all");
        if(obj == all_obj){//全部
            if(obj.parentNode.className.indexOf("cur") != -1){//如果已经选中的
                return;
            }
            obj.parentNode.className = "cur";
            var n = obj.parentNode.parentNode.firstChild;
            for(; n; n = n.nextSibling) {
                if(n.nodeType === 1 && n !== obj.parentNode) {
                    n.className = "";
                }
            }
        }else{
            if(obj.parentNode.className.indexOf("cur") != -1){//如果已经选中的
                obj.parentNode.className = "";//先取消选中,再判断是否一个选中的都没有
                var n = obj.parentNode.parentNode.firstChild;
                var itemSelectedCount = 0;
                for(; n; n = n.nextSibling) {
                    if(n.nodeType === 1 && n !== all_obj.parentNode) {
                        if(n.className.indexOf("cur") != -1){
                            itemSelectedCount++;
                        }
                    }
                }
                if(itemSelectedCount == 0){//如果一个选中的子项都没有
                    all_obj.parentNode.className = "cur";
                }else{
                    all_obj.parentNode.className = "";
                }
                
            }else{//如果未选中,则选中,并将去掉“全部”的选中状态
                obj.parentNode.className = "cur";
                all_obj.parentNode.className = "";
            }
        }
        yumi_partner_ajax();
    }
    
